ne {\ncolor: #010080;\nbackground-color: #010080;\nheight: 1px;\nwidth: 25%;\n}\n\n/* separates maxcode from message content */\nHR.maxline {\ncolor: {defcolor3};\nbackground-color: {defcolor3};\nheight: 1px;\n}\n\n/* normal horizontal line */\nHR.line {\ncolor: {defcolor3};\nbackground-color: {defcolor3};\nheight: 1px;\n}\n\n/* normal text outside the forum */\n.tnormal {\nfont-family : {font-family}; \nfont-size : {deffont-size1}; \nfont-style : normal; \ncolor : #000000; \n}\n\n/* small text outside the forum */\n.tsmall {\nfont-family : {font-family}; \nfont-size : {deffont-size2}; \ncolor : #000000; \n}\n\n/* small message font-properties*/\n.mnormal1 {\nfont-family : {font-family};\nfont-size : 10px; \nfont-style : normal; \ncolor : #010080; \n}\n\n/* normal message font-properties 2*/\n.mnormal {\nfont-family : {font-family};\nfont-size : 12px; \nfont-style : normal; \ncolor : #010080; \n}\n\n/* big message font-properties*/\n.mnormal3 {\nfont-family : {font-family};\nfont-size : 14px; \nfont-style : normal; \ncolor : #010080; \n}\n\n/* normal forum font-properties*/\n.fnormal {\nfont-family : {font-family};\nfont-size : {deffont-size1}; \nfont-style : normal; \ncolor : #010080; \n}\n\n/* small forum font-properties*/\n.fsmall {\nfont-family : {font-family}; \nfont-size : {deffont-size2}; \ncolor : #010080; \n}\n\n/* heading of table */\n.fheading {\nfont-family : {font-family}; \nfont-size : {deffont-size1}; \nfont-weight : bold; \ncolor : {defcolor1};\nbackground : url(\"../{images_url}/top.png\") #2020f0;\n}\n\n/* cat. description, and other table subtitles */\n.fdiz {\nfont-family : {font-family}; \nfont-size : 11px; \nfont-weight : bold; \ncolor : {defcolor1};\nbackground-color: #4545ff;\n}\n\n/* category link */\na.catlink:link, a.catlink:visited {\nfont-family : {font-family}; \nfont-size : 12px; \nfont-weight : bold;\ntext-decoration : underline; \ncolor : {defcolor1}; \n}\n\na.catlink:active, a.catlink:hover {\nfont-family : {font-family}; \nfont-size : 12px; \nfont-weight : bold;\ntext-decoration : none; \ncolor : {defcolor1}; \n}\n\n/* small link */\na.sml:link, a.sml:visited {\nfont-family : {font-family}; \nfont-size : {deffont-size2};\ntext-decoration : none; \ncolor : {defcolor2}; \nfont-weight: normal;\n}\n\na.sml:active, a.sml:hover {\nfont-family : {font-family}; \nfont-size : {deffont-size2};\ntext-decoration : underline; \ncolor : #0050FF; \nfont-weight: normal;\n}\n\n/* default link */\na.nrml:link, a.nrml:visited {\nfont-family : {font-family};\nfont-size : {deffont-size1}; \ncolor: {defcolor2};\nfont-weight: normal;\n}\n\na.nrml:active, a.nrml:hover {\nfont-family: {font-family};\nfont-size: {deffont-size1}; \ntext-decoration: none;\ncolor: #0050FF;\nfont-weight: normal;\n}\n\n/* threadlink */\na.threadlink:link, a.threadlink:visited {\ncolor: #202080;\nfont-family: {font-family};\nfont-size: {deffont-size1};\nfont-weight: bold;\ntext-decoration: underline;\n}\n\na.threadlink:active, a.threadlink:hover {\ncolor: #202080;\nfont-family: {font-family};\nfont-size: {deffont-size1};\nfont-weight: bold;\ntext-decoration: none;\n}\n\n/* refresh, go back, new thread buttonlink */\na.bttn:link, a.bttn:visited {\nbackground : url(\"../{images_url}/bttn_up.png\"); \nfont-family : {font-family}; \nfont-weight : bold; \nfont-size : 11px; \ncolor : {defcolor1};\nline-height : 27px; \nheight : 30px; \nwidth : 100px; \ntext-decoration : none;\n}\n\na.bttn:active, a.bttn:hover {\nbackground : url(\"../{images_url}/bttn_down.png\");  \ntext-decoration : none;\ncolor : {defcolor1};\n}\n\n/* locked button */\n.locked {\nbackground : url(\"../{images_url}/bttn_locked.png\"); \nfont-family : {font-family}; \nfont-weight : bold; \nfont-size : 10px; \ncolor : #fff000; \nline-height : 25px; \nheight : 30px; \nwidth : 100px; \n}\n\n/* submit button */\n.bttn1 {\nfont-family: {font-family};\nfont-size: {deffont-size1};\nfont-weight: bold;\nbackground-color: {defcolor3};\nborder-bottom: 1px solid #000000;\nborder-right: 1px 